import { useState, useEffect, useRef } from 'react';
import { Send, Loader2, User, Crown, CircleEllipsis } from 'lucide-react';

// Main App component for the Saints Chatbot
const App = () => {
  // State for storing the chat history, input value, and loading status
  const [messages, setMessages] = useState([
    {
      role: 'ai',
      text: "Who Dat! I'm your go-to source for all things Saints. Ask me anything about the team's history, players, or big games. Let's talk some black and gold!"
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Function to scroll to the bottom of the message list
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Scroll to the bottom whenever messages are updated
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Function to handle the form submission and API call
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    const userMessage = { role: 'user', text: input };
    setMessages((prevMessages) => [...prevMessages, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      // Craft the prompt for the AI model, including the Saints persona
      const prompt = `You are a huge New Orleans Saints fan and a historian of the team. Respond to all questions from the perspective of a passionate fan with deep knowledge of the team's history. Use a conversational, friendly, and enthusiastic tone. Your goal is to be helpful and informative while maintaining this persona. End your responses with a "Who Dat!". The user asks: ${input}`;

      // Prepare the chat history for the API call
      const chatHistory = messages.map(msg => ({
        role: msg.role === 'ai' ? 'model' : 'user',
        parts: [{ text: msg.text }]
      }));
      chatHistory.push({ role: 'user', parts: [{ text: prompt }] });

      const payload = { contents: chatHistory };
      const apiKey = ""; // Canvas will provide this key at runtime
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      if (!response.ok) {
        throw new Error(`API error: ${response.statusText}`);
      }

      const result = await response.json();

      if (result.candidates && result.candidates.length > 0 &&
          result.candidates[0].content && result.candidates[0].content.parts &&
          result.candidates[0].content.parts.length > 0) {
        const aiResponse = result.candidates[0].content.parts[0].text;
        setMessages((prevMessages) => [...prevMessages, { role: 'ai', text: aiResponse }]);
      } else {
        console.error("Unexpected API response structure:", result);
        setMessages((prevMessages) => [...prevMessages, { role: 'ai', text: 'Oops! Something went wrong. Try asking again!' }]);
      }
    } catch (error) {
      console.error('Error fetching from Gemini API:', error);
      setMessages((prevMessages) => [...prevMessages, { role: 'ai', text: 'My apologies, there was an issue connecting to the servers. Please give it another try!' }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Render the chat interface
  return (
    <div className="flex flex-col h-screen bg-neutral-900 text-white font-sans">
      {/* Header for the chat app */}
      <header className="p-4 bg-yellow-500 text-black text-center font-bold text-lg rounded-b-lg shadow-lg">
        Saints Superfan Chatbot
      </header>

      {/* Main chat messages container */}
      <main className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`flex items-start gap-3 ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            {msg.role === 'ai' && (
              <div className="flex-shrink-0 w-8 h-8 rounded-full bg-black flex items-center justify-center border-2 border-yellow-500">
                <Crown size={20} className="text-yellow-500" />
              </div>
            )}
            <div
              className={`p-4 rounded-3xl max-w-lg shadow-lg ${
                msg.role === 'user'
                  ? 'bg-yellow-500 text-black rounded-tr-none'
                  : 'bg-neutral-800 text-white rounded-tl-none'
              }`}
            >
              <p>{msg.text}</p>
            </div>
            {msg.role === 'user' && (
              <div className="flex-shrink-0 w-8 h-8 rounded-full bg-yellow-500 flex items-center justify-center">
                <User size={20} className="text-black" />
              </div>
            )}
          </div>
        ))}
        {isLoading && (
          <div className="flex justify-start">
            <div className="flex items-center gap-3">
              <div className="flex-shrink-0 w-8 h-8 rounded-full bg-black flex items-center justify-center border-2 border-yellow-500">
                <Crown size={20} className="text-yellow-500" />
              </div>
              <div className="p-4 rounded-3xl max-w-lg shadow-lg bg-neutral-800 text-white rounded-tl-none">
                <div className="flex items-center space-x-2">
                  <span className="animate-pulse">Thinking...</span>
                  <Loader2 className="animate-spin" size={18} />
                </div>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </main>

      {/* Input form for sending messages */}
      <form onSubmit={handleSubmit} className="p-4 bg-neutral-900 border-t border-neutral-700 flex items-center gap-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask me something about the Saints..."
          className="flex-1 p-3 rounded-full bg-neutral-800 text-white placeholder-neutral-500 focus:outline-none focus:ring-2 focus:ring-yellow-500 transition-all"
          disabled={isLoading}
        />
        <button
          type="submit"
          className="p-3 bg-yellow-500 text-black rounded-full shadow-lg hover:bg-yellow-400 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          disabled={isLoading || !input.trim()}
        >
          <Send size={24} />
        </button>
      </form>
    </div>
  );
};

export default App;
