import React, { useState, useEffect, useRef } from 'react';

// Main App component
const App = () => {
  // State to hold the chat history
  const [chatHistory, setChatHistory] = useState([
    { role: 'model', parts: [{ text: "Who Dat! I'm your go-to source for all things Saints. Ask me anything about the team's history, players, or big games. Let's talk some black and gold!" }] }
  ]);
  // State to hold the current user input
  const [userInput, setUserInput] = useState('');
  // State to track if an API call is in progress
  const [isLoading, setIsLoading] = useState(false);
  // State to hold any API-related error message
  const [apiError, setApiError] = useState(null);

  // Create a ref for the chat container to handle scrolling
  const chatContainerRef = useRef(null);

  // useEffect to scroll to the bottom of the chat when new messages are added
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatHistory, apiError]);

  // Function to handle sending a new message
  const handleSendMessage = async () => {
    // If the input is empty or we're already loading, do nothing
    if (!userInput.trim() || isLoading) return;

    // Add the user's message to the chat history
    const newUserMessage = { role: 'user', parts: [{ text: userInput }] };
    setChatHistory(prevChatHistory => [...prevChatHistory, newUserMessage]);
    
    // Clear the input field, any previous error, and set loading state to true
    setUserInput('');
    setApiError(null);
    setIsLoading(true);

    try {
      // The prompt for the API call will include the user's query
      const prompt = `You are a chatbot that is a New Orleans Saints superfan. Your persona is enthusiastic and knowledgeable. Respond to user queries about the Saints team's history, players, and big games. User asks: ${userInput}`;
      
      // Prepare the payload for the Gemini API
      const payload = {
        contents: [{ role: "user", parts: [{ text: prompt }] }],
      };
      
      // Replace with your actual API key here
      const apiKey = "AIzaSyCjYVpRM2pUYkmaP6-My3oU36D93T7SJkI";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

      // Make the API call with exponential backoff for retries
      let response;
      let retries = 0;
      const maxRetries = 5;
      const baseDelay = 1000; // 1 second

      while (retries < maxRetries) {
        try {
          console.log(`Attempting API call #${retries + 1}...`);
          response = await fetch(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
          });
          if (response.ok) {
            console.log("API call successful!");
            break; // Success, exit the loop
          } else {
            console.error(`API call failed with status: ${response.status} - ${response.statusText}`);
            retries++;
            const delay = baseDelay * (2 ** retries);
            console.log(`Retrying in ${delay}ms...`);
            await new Promise(res => setTimeout(res, delay));
          }
        } catch (error) {
          console.error('Fetch error during API call:', error);
          retries++;
          const delay = baseDelay * (2 ** retries);
          console.log(`Retrying in ${delay}ms...`);
          await new Promise(res => setTimeout(res, delay));
        }
      }
      
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to get a response from the API after multiple retries. Status: ${response.status}. Message: ${errorText}`);
      }

      const result = await response.json();
      
      // Check if the response contains valid content
      if (result.candidates && result.candidates.length > 0 &&
          result.candidates[0].content && result.candidates[0].content.parts &&
          result.candidates[0].content.parts.length > 0) {
        const text = result.candidates[0].content.parts[0].text;
        // Split the text by sentences to simulate typing in multiple bubbles
        const sentences = text.match(/[^.!?]+[.!?]|\S+/g) || [text];
        
        let tempChatHistory = [...chatHistory, newUserMessage];
        
        for (const sentence of sentences) {
            // Update the state with the latest message as a new bubble
            tempChatHistory = [...tempChatHistory, { role: 'model', parts: [{ text: sentence.trim() }] }];
            setChatHistory(tempChatHistory);
            await new Promise(res => setTimeout(res, 500));
        }
      } else {
        // Handle unexpected response format
        throw new Error('Unexpected API response format.');
      }
    } catch (error) {
      console.error('Error generating content:', error);
      // Set an error state instead of adding a message to the chat
      setApiError("My apologies, there was an issue connecting to the servers. Please give it another try!");
    } finally {
      // Set loading state back to false
      setIsLoading(false);
    }
  };

  // UI for the chatbot
  return (
    <div className="flex flex-col h-screen bg-gray-100 font-inter antialiased">
      <div 
        className="chat-container" 
        ref={chatContainerRef} 
        style={{ flex: 1, overflowY: 'auto', padding: '1rem', display: 'flex', flexDirection: 'column', gap: '12px' }}
      >
        {chatHistory.map((message, index) => (
          <div
            key={index}
            className={`message-bubble ${
              message.role === 'user' ? 'message-user' : 'message-bot'
            }`}
          >
            <p>{message.parts[0].text}</p>
          </div>
        ))}
        {isLoading && (
          <div className="message-bubble message-bot">
            <span className="animate-pulse">...</span>
          </div>
        )}
        {apiError && (
            <div className="message-bubble message-bot">
                <p style={{ color: 'red' }}>{apiError}</p>
            </div>
        )}
      </div>

      <div className="p-4 bg-white border-t border-gray-200 shadow-lg sticky bottom-0 w-full">
        <div className="flex items-center max-w-2xl mx-auto">
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
            className="flex-1 p-3 rounded-l-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-600 transition-colors"
            placeholder="Ask me about the Saints..."
            disabled={isLoading}
          />
          <button
            onClick={handleSendMessage}
            className="p-3 bg-blue-600 text-white rounded-r-lg hover:bg-blue-700 transition-colors disabled:bg-gray-400"
            disabled={isLoading}
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
